mapper.update:
update wallet_change_record
    set status = #{newStatus}
where
    id  =#{id}
    and member_id = #{memberId}
    and status = #{oldStatus}

update wallet_change_record
    set tcc_status = #{newStatus}
where
    id = #{id}
    and member_id = #{memberId}
    and tcc_status = #{oldStatus}
